    public StorageResource getAllGenes(String dna) {
        dna = dna.toUpperCase();
        StorageResource geneList = new StorageResource();
        int startIndex = 0;
        while (true) {
            String gene = findGene(dna, startIndex);
            if (gene.equals("")) {
                break;
            } else {
                geneList.add(gene);
                startIndex = dna.indexOf(gene, startIndex) + gene.length();
            }
        }
        return geneList;
    }
    
