    public void printAllGenes(String dna) {
        int startIndex = 0;
        while (true) {
            String gene = findGene(dna, startIndex);
            if (gene.equals("")) {
                break;
            } else {
                System.out.println(gene);
                startIndex = dna.indexOf(gene, startIndex) + gene.length();
            }
        }
    }
      
    
    public String findGene(String dna, int where) {
        dna = dna.toUpperCase();
        // find first occurance of ATG
        int startIndex = dna.indexOf("ATG", where);
        // if startIndex is -1 return ""
        if (startIndex == -1) {
            return "";
        }
        // use taaIndex, tagIndex, tgaIndex to store stopCodons
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");
        // minIndex for the smallest value
        //int temp = Math.min(taaIndex, tagIndex);
        //int minIndex = Math.min(temp, tgaIndex);
        int minIndex = 0;
        if (taaIndex == -1 ||
            (tgaIndex != -1 && tgaIndex < taaIndex)) {
                minIndex = tgaIndex;
            }
        else {
            minIndex = taaIndex;
        }
        
        if (minIndex == -1 ||
            (tagIndex != -1 && tagIndex < minIndex)) {
                minIndex = tagIndex;
            }
        // if minIndex is dna.length() nothing found, return empry string
        if (minIndex == -1) {
            return "";
        } else {
            return dna.substring(startIndex, minIndex+3);
        }
    }
    
    public int findStopCodon(String dna, int startIndex, String stopCodon) {
        // returns the index of the first occurrence of stopCodon
        int currIndex = dna.indexOf(stopCodon, startIndex + 3);
        while (currIndex != -1) {
            int diff = currIndex - startIndex;
            if (diff % 3 == 0) {
                return currIndex;
            }
            else {
                currIndex = dna.indexOf(stopCodon, currIndex + 1);
            }
        }
        // no such stopCodon, this method returns the length of the dna strand
        return -1;
    }
    
    public void testFindStopCodon() {
        //                             0123456789
        int stopIndex = findStopCodon("XXXYYYZZZTAAxxxyyyzzzTAA", 0, "TAA");
        if (stopIndex != 9) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
        //                         0123456789
        stopIndex = findStopCodon("XXXYYYZZTAA", 0, "TAA");
        if (stopIndex != "XXXYYYZZTAA".length()) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
        //                         0123456789
        stopIndex = findStopCodon("", 0, "TAA");
        if (stopIndex != "".length()) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
        //                         0123456789
        stopIndex = findStopCodon("XXSSSYYYTAA", 2, "TAA");
        if (stopIndex != 8) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
    }
