    public void printAllGenes(String dna) {
        int startIndex = 0;
        while (true) {
            String gene = findGene(dna, startIndex);
            if (gene == "") {
                break;
            } else {
                System.out.println(gene);
                startIndex = dna.indexOf(gene, startIndex) + gene.length();
            }
        }
    }
    
    public String findGene(String dna, int where) {
        dna = dna.toUpperCase();
        int startIndex = dna.indexOf("ATG", where);
        if (startIndex == -1) {
            return "";
        }
        
        int stopIndex = findStopCodon(dna, startIndex);
        if (stopIndex == -1) {
            return "";
        } else {
            return dna.substring(startIndex, stopIndex+3);
        }
    }
    
    public int findStopCodon(String dna, int index) {
        int stop1 = dna.indexOf("tag", index);
        if (stop1 == -1 || (stop1 - index) % 3 != 0) {
            stop1 = dna.length();
        }
        int stop2 = dna.indexOf("tga", index);
        if (stop2 == -1 || (stop2 - index) % 3 != 0) {
            stop2 = dna.length();
        }
        int stop3 = dna.indexOf("taa", index);
        if (stop3 == -1 || (stop3 - index) % 3 != 0) {
            stop3 = dna.length();
        }
        if (stop1 == dna.length() && stop2 == dna.length() && stop3 == dna.length()) {
            return -1;
        }
        else {
            return Math.min(stop1, Math.min(stop2, stop3));
        }
    }
    
