    public void testFindStopCodon() {
        //                             0123456789
        int stopIndex = findStopCodon("XXXYYYZZZTAA", 0);
        if (stopIndex != 9) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
        //                         0123456789
        stopIndex = findStopCodon("XXXYYYZZTAA", 0);
        if (stopIndex != "XXXYYYZZTAA".length()) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
        //                         0123456789
        stopIndex = findStopCodon("", 0);
        if (stopIndex != "".length()) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
        //                         0123456789
        stopIndex = findStopCodon("XXSSSYYYTAA", 2);
        if (stopIndex != 8) {
            System.out.println("stopIndex: " + stopIndex);
        } else {
            System.out.println("fine!");
        }
    }
